package org.company.esc.tugasakhir;import android.content.Intent;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.RecyclerView;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.bumptech.glide.Glide;import com.bumptech.glide.request.RequestOptions;import java.util.ArrayList;import static org.company.esc.tugasakhir.MainActivity.EXTRA_ASAL;import static org.company.esc.tugasakhir.MainActivity.EXTRA_DESKRIPSI;import static org.company.esc.tugasakhir.MainActivity.EXTRA_NAME;import static org.company.esc.tugasakhir.MainActivity.EXTRA_URL;public class DetailAcaraActivity extends AppCompatActivity{    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {         super.onCreate(savedInstanceState);         setContentView(R.layout.activity_detail_acara);        Intent intent = getIntent();        String imageUrl = intent.getStringExtra(EXTRA_URL);        String namaAcara = intent.getStringExtra(EXTRA_NAME);        String asalAcaara= intent.getStringExtra(EXTRA_ASAL);        String deskripsiAcara = intent.getStringExtra(EXTRA_DESKRIPSI);        ImageView imageView = findViewById(R.id.img_item_foto);        TextView textViewNama = findViewById(R.id.tv_item_nama);        TextView textViewAsal = findViewById(R.id.tv_item_asal);        TextView textViewDeskripsi = findViewById(R.id.tv_item_deskripsi);        Glide.with(this).load(imageUrl).into(imageView);        textViewNama.setText(namaAcara);        textViewAsal.setText(asalAcaara);        textViewDeskripsi.setText(deskripsiAcara);    }}