package org.company.esc.tugasakhir;import android.graphics.drawable.Drawable;import android.support.annotation.NonNull;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import com.bumptech.glide.Glide;import com.bumptech.glide.request.RequestOptions;import com.bumptech.glide.request.target.Target;import java.util.ArrayList;public class GridAcaraAdapter extends RecyclerView.Adapter<GridAcaraAdapter.GridViewHolder> {    private ArrayList<Acara> listAcara;    public GridAcaraAdapter (ArrayList<Acara> list){        this.listAcara = list;    }    @NonNull    @Override    public GridViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int viewType) {        View view = LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.item_grid_event, viewGroup,false );        return new GridViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull  GridViewHolder ViewHolder, int position) {        Glide.with(ViewHolder.itemView.getContext())                .load(listAcara.get(position).getPhoto())                .apply(new RequestOptions().override(350,550))                .into(ViewHolder.imgAcara);    }    @Override    public int getItemCount() {        return listAcara.size();    }    class GridViewHolder extends  RecyclerView.ViewHolder {        ImageView imgAcara;       GridViewHolder(@NonNull View itemView) {            super(itemView);            imgAcara = itemView.findViewById(R.id.img_item_foto);        }    }}