package org.company.esc.tugasakhir;import android.support.annotation.NonNull;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.bumptech.glide.Glide;import com.bumptech.glide.request.RequestOptions;import java.util.ArrayList;public class ListAcaraAdapter extends RecyclerView.Adapter<ListAcaraAdapter.ListViewHolder> {    private OnItemClickCallback onItemClickCallback;    public  void setOnItemClickCallback (OnItemClickCallback onItemClickCallback){        this.onItemClickCallback = onItemClickCallback;    }    private ArrayList<Acara> listAcara;    public ListAcaraAdapter (ArrayList<Acara> list){        this.listAcara = list;    }    @NonNull    @Override    public ListViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int viewType) {        View view = LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.item_row_event, viewGroup, false);        return new ListViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull final ListViewHolder holder, int position) {Acara acara = listAcara.get(position);        Glide.with(holder.itemView.getContext())                .load(acara.getPhoto())                .apply(new RequestOptions().override(55,55))                .into(holder.imgAcara);        holder.tvNama.setText(acara.getName());        holder.tvAsal.setText(acara.getFrom());        holder.itemView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                onItemClickCallback.onItemClicked(listAcara.get(holder.getAdapterPosition()));            }        });    }    @Override    public int getItemCount() {        return listAcara.size();    }    public class ListViewHolder extends RecyclerView.ViewHolder {        ImageView imgAcara;        TextView tvNama, tvAsal;        public ListViewHolder(@NonNull View itemView) {            super(itemView);            imgAcara = (ImageView) itemView.findViewById(R.id.img_item_foto);                    tvNama = (TextView) itemView.findViewById(R.id.tv_item_nama);                    tvAsal = (TextView) itemView.findViewById(R.id.tv_item_asal);        }    }    public interface OnItemClickCallback {        void onItemClicked (Acara data);    }}