package org.company.esc.tugasakhir;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.GridLayoutManager;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.Menu;import android.view.MenuItem;import java.util.ArrayList;public class MainActivity extends AppCompatActivity {private RecyclerView rvAcara;private ArrayList<Acara> list = new ArrayList<>();    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        rvAcara = findViewById (R.id.rv_event);        rvAcara.setHasFixedSize(true);        list.addAll(AcaraData.getListData());        showRecylerList();    }    private void showRecylerList() {        rvAcara.setLayoutManager(new LinearLayoutManager(this));        ListAcaraAdapter listAcaraAdapter = new ListAcaraAdapter(list);        rvAcara.setAdapter(listAcaraAdapter);    }    private void showRecyclerGrid(){        rvAcara.setLayoutManager(new GridLayoutManager(this, 2));        GridAcaraAdapter gridAcaraAdapter = new GridAcaraAdapter(list);        rvAcara.setAdapter(gridAcaraAdapter);    }    private void showRecyclerCardView(){        rvAcara.setLayoutManager(new LinearLayoutManager(this));        CardViewAdapter cardViewHeroAdapter = new CardViewAdapter(list);        rvAcara.setAdapter(cardViewHeroAdapter);    }    @Override    public boolean onCreateOptionsMenu(Menu menu) {        getMenuInflater().inflate(R.menu.main_menu, menu);        return super.onCreateOptionsMenu(menu);    }    @Override    public boolean onOptionsItemSelected(MenuItem item) {        setMode(item.getItemId());        return super.onOptionsItemSelected(item);    }    private void setMode(int itemId) {        switch (itemId){            case R.id.list_action:                showRecylerList();                break;            case R.id.grid_action:                showRecyclerGrid();                break;            case R.id.cardview_action:                showRecyclerCardView();                break;        }    }}