package org.company.esc.tugasakhir;import android.content.Intent;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.GridLayoutManager;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.Menu;import android.view.MenuItem;import android.view.View;import android.widget.ImageView;import android.widget.Toast;import java.util.ArrayList;public class MainActivity extends AppCompatActivity implements CardViewAdapter.OnItemClickListener {    public static final String EXTRA_URL = "imgaeURL";    public static final String EXTRA_NAME = "acaraNama";    public static final String EXTRA_ASAL = "asalAcara";    public static final String EXTRA_DESKRIPSI = "deskripsiAcara";private RecyclerView rvAcara;private ArrayList<Acara> list = new ArrayList<>();    private String title = "Mode List";    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        setActionBarTitle(title);        rvAcara = findViewById (R.id.rv_event);        rvAcara.setHasFixedSize(true);        setActionBarTitle("Mode List");        list.addAll(AcaraData.getListData());        showRecylerList();    }    private void showRecylerList() {        rvAcara.setLayoutManager(new LinearLayoutManager(this));        ListAcaraAdapter listAcaraAdapter = new ListAcaraAdapter(list);        rvAcara.setAdapter(listAcaraAdapter);        listAcaraAdapter.setOnItemClickCallback(new ListAcaraAdapter.OnItemClickCallback() {            @Override            public void onItemClicked(Acara data) {                showAcaraGambar(data);            }        });    }    private void showRecyclerGrid(){        rvAcara.setLayoutManager(new GridLayoutManager(this, 2));        GridAcaraAdapter gridAcaraAdapter = new GridAcaraAdapter(list);        rvAcara.setAdapter(gridAcaraAdapter);    }    private void showRecyclerCardView(){        rvAcara.setLayoutManager(new LinearLayoutManager(this));        CardViewAdapter cardViewAdapter = new CardViewAdapter(list);        rvAcara.setAdapter(cardViewAdapter);        cardViewAdapter.setOnItemClickListener(MainActivity.this);    }        private void setActionBarTitle(String title){        if (getSupportActionBar() != null){            getSupportActionBar().setTitle(title);        }        }        private void showAcaraGambar (Acara acara){            Toast.makeText(this, "Kamu Mimilih" + acara.getName(), Toast.LENGTH_SHORT).show();        }    @Override    public boolean onCreateOptionsMenu(Menu menu) {        getMenuInflater().inflate(R.menu.main_menu, menu);        return super.onCreateOptionsMenu(menu);    }    @Override    public boolean onOptionsItemSelected(MenuItem item) {        setMode(item.getItemId());        return super.onOptionsItemSelected(item);    }    private void setMode(int itemId) {        switch (itemId){            case R.id.list_action:                title = "Mode List";                showRecylerList();                break;            case R.id.grid_action:                title = "Mode Grid";                showRecyclerGrid();                break;            case R.id.cardview_action:                title = "Mode CardView";                showRecyclerCardView();                break;        }    }    @Override    public void onItemClick(int position) {        Intent detailIntent = new Intent(this, DetailAcaraActivity.class);        Acara clickedItem = list.get(position);        detailIntent.putExtra(EXTRA_URL, clickedItem.getPhoto());        detailIntent.putExtra(EXTRA_NAME, clickedItem.getName());        detailIntent.putExtra(EXTRA_ASAL, clickedItem.getFrom());        detailIntent.putExtra(EXTRA_DESKRIPSI, clickedItem.getDeskripsi());        startActivity(detailIntent);    }}